// Lightweight CSS-based background to replace heavy WebGL 3D backgrounds
// This prevents WebGL context loss issues

export function SimpleBackground() {
  return (
    <div style={{
      position: 'fixed',
      inset: 0,
      zIndex: -10,
      background: 'linear-gradient(to bottom right, #0f172a, #581c87, #0f172a)'
    }}>
      <div style={{
        position: 'absolute',
        inset: 0,
        backgroundImage: 'url("data:image/svg+xml;base64,")',
        opacity: 0.2
      }}></div>
    </div>
  );
}
